    private void txtVelocityKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtVelocityKeyPressed
        char validar = evt.getKeyChar();
        this.verificationJustNumbers(validar, txtVelocity);
    }//GEN-LAST:event_txtVelocityKeyPressed

    private void txtAngleKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtAngleKeyPressed
        char validar = evt.getKeyChar();
        this.verificationJustNumbers(validar, txtAngle);
    }//GEN-LAST:event_txtAngleKeyPressed

    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        Projectile projectile =new Projectile();
        
        projectile.setInicialVelocity(Float.parseFloat(txtVelocity.getText()));
        projectile.setAngle(Double.parseDouble(txtAngle.getText()));
        projectile.calculateDistance();
        lblXmax.setText(String.valueOf(projectile.getX()));
        projectile.savetoFile();
        
    }//GEN-LAST:event_btnCalculateActionPerformed

    
       public void verificationJustNumbers(char charValidation, JTextField nameOfTheTextField)
    {
        if(Character.isDigit(charValidation))
        {
            nameOfTheTextField.setBackground(Color.WHITE);
        }
        else
        {
            nameOfTheTextField.setBackground(Color.red);
            
        }
    }    
    
    
    
